mapper.delete:
delete from shopping_cart


        and id = #{id}

    and user_id = #{userId}

mapper.select:
select
id, name, image, user_id, dish_id, setmeal_id, dish_flavor, number, amount, create_time
from
shopping_cart


        and user_id = #{userId}


        and dish_id = #{dishId}


        and dish_flavor = #{dishFlavor}


        and setmeal_id = #{setmealId}
